import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = () => {
  //   const [enteredTitle, setEnteredTitle] = useState("");
  //   const [eneteredAmount, setEneteredAmount] = useState("");
  //   const [eneteredDate, setEneteredDate] = useState("");

  const [userInput, setUserInput] = useState({
    enteredTitle: "",
    enteredAmout: "",
    enteredDate: "",
  });

  const titleChangeHander = (event) => {
    // setEnteredTitle(event.target.value);
    setUserInput({
        ...userInput, enteredTitle: event.target.value,
    })
  };

  const amountChangeHandler = (event) => {
    // setEneteredAmount(event.target.value);
    setUserInput({
        ...userInput,
        enteredAmout: event.target.value
    })
  };

  const dateChangeHandler = (event) => {
    // setEneteredDate(event.target.value);
    setUserInput({
        ...userInput,
        enteredDate: event.target.value
    })
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHander} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2022-12-31" />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
